/* Square - function component - meaning it's a full component but not a class */ 
function Square(props) {
  return (
    <button className="square" onClick={props.onClick}>
      {props.value}
    </button>
  );
}

/* Board class */ 
class Board extends React.Component {

  constructor(props){
    super(props);

    // Initialize board state to have 9 empty squares (array)
    this.state = {
      squares : Array(9).fill(null),
      xIsNext : true
    };
  }

  // Called by square - see the square props onClick() val
  handleClick(i) {

    // Grab current square and set the quare's state 
    const squares = this.state.squares.slice();
    squares[i] = this.state.xIsNext ? 'X' : 'O';

    // Store state of squares to board 
    this.setState({
      squares : squares,
      xIsNext : !this.state.xIsNext
    });

    // mParticle tracking 
    /* eslint-disable  */
    // window.mParticle.logEvent("Square Clicked", mParticle.EventType.Other, {"Square Character" : squares[i], "Square Number" : i});
    /* eslint-enable  */
  }

  renderSquare(i) {
    // Render square based off boards square state 
    // Passes value to square AND handleClick function (parent/board function to square)
    return (
            <Square 
              value={this.state.squares[i]} 
              onClick={() => this.handleClick(i)}
            />
          );
  }

  render() {
    const status = 'Next player: '+ (this.state.xIsNext ? 'X' : 'O');

    return (
      <div>
        <div className="status">{status}</div>
        <div className="board-row">
          {this.renderSquare(0)}
          {this.renderSquare(1)}
          {this.renderSquare(2)}
        </div>
        <div className="board-row">
          {this.renderSquare(3)}
          {this.renderSquare(4)}
          {this.renderSquare(5)}
        </div>
        <div className="board-row">
          {this.renderSquare(6)}
          {this.renderSquare(7)}
          {this.renderSquare(8)}
        </div>
      </div>
    );
  }
}

/* Game class */ 
class Game extends React.Component {
  render() {
    return (
      <div className="game">
        <div className="game-board">
          <Board />
        </div>
        <div className="game-info">
          <div>{/* status */}</div>
          <ol>{/* TODO */}</ol>
        </div>
      </div>
    );
  }
}

